const CardComponent = ({ title, description, link }) => (
  <div className='card bg-blue-500 text-white p-4'>
    <h2 className='text-xl'>{title}</h2>
    <p className='mt-2'>{description}</p>
    <a href={link} className='mt-4 inline-block bg-white text-blue-500 px-4 py-2 rounded'>See More</a>
  </div>
);

const ButtonComponent = ({ title, link }) => (
  <button onClick={() => window.location.href = link} className='bg-blue-500 text-white px-4 py-2 rounded'>{title}</button>
);

const NavbarComponent = ({ title, links }) => (
  <nav className='bg-blue-500 text-white p-4'>
    <h2 className='text-xl'>{title}</h2>
    <ul className='flex space-x-4'>
      {links.map((link, index) => (
        <li key={index}>
          <a href={link.href} className='text-white'>{link.text}</a>
        </li>
      ))}
    </ul>
  </nav>
);
